package gestao.repository;import gestao.model.Hospital;import gestao.model.Leito;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import java.util.List;import java.util.Optional;@Repositorypublic interface LeitoRepository extends JpaRepository<Leito, Long> {    @Query("select l from Leito l where l.isLivre = true and l.hospital = ?1")    Optional<List<Leito>> findAllByIsLivreAndHospital(Hospital hospital);}